# Guide to what the different integers represent as arguments:

# number: 1 = SG; 2 = PL
# possessorPerson: 0 = none; 1 = 1; 2 = 2; 3 = 3
# possessorNumber: 1 = SG; 2 = PL
# case: 0 = NOM; 1 = ACC; 2 = GEN; 3 = DAT; 4 = LOC; 5 = ABL; 6 = ADL

class englishWord():

    def inflect(stem, number, possessorPerson, possessorNumber, case):
        if not (possessorPerson == 0 and possessorNumber != 1):
            return(englishWord.markCASE(case, englishWord.markPOS(possessorPerson, possessorNumber, englishWord.markNUM(number, stem))))
        
    def markNUM(number, stem):
        
        if number == 2:
            if re.search("[(ch)(sh)xjsz]$", stem) != None:
                return stem + "es"
            elif re.search("[bcdfghjklmnpqrstvwxz]y$", stem) != None:
                return stem[:len(stem)-1] + "ies"
            else: return stem + "s"
        else: return stem

    def markPOS(person, number, stem):
        
        if person == 1 and number == 1:
            return "my " + stem
        if person == 1 and number == 2:
            return "our " + stem
        if person == 2:
            return "your " + stem
        if person == 3 and number == 1:
            #TODO How to deal with gender?
            return "its " + stem
        if person == 3 and number == 2:
            return "their " + stem
            
        return "the " + stem

    def markCASE(case, stem):
        #TODO decide how to implement English equivalents of Tuvan cases
        if case == 2:
            # <'s> genitive or <of > genitive?
            if stem[-1] == "s":
                return stem + "'"
            return stem + "'s"
        if case == 3:
            # <to > dative or <> dative?
            return "to " + stem
        if case == 4:
            return "in " + stem
        if case == 5:
            return "from " + stem
        if case == 6:
            print("Not yet done.")

        return stem

class tuvanWord():

    # These lists contain the allophones of different archiphonemes.
    # This is a more compact and less confusing approach than using
    # lots of "if" statements.

    I  = [["и", "ү"], ["ы", "у"]]     
    A  = ["е", "а"]

    L  = ["л", "н", "т", "д", "л"]
    N  = ["н", "н", "т", "д", "н"]
    D  = ["д", "д", "т", "д", "д"] # |D| which does not nasalize
    D2 = ["д", "н", "т", "д", "д"]
    G  = ["г", "г", "к", "г", "г"]
    # J = ?

    def getBackness(stem):
        # 1 = +bk; 0 = -bk
        if re.search("[еэиөү][^аяоёыую]*$", stem) != None:
            return 0
        return 1

    def getRounding(stem):
        # 1 = +rd; 0 = -rd
        if re.search("[аяыиеэ][^оёуюөү]*$", stem) != None:
            return 0
        return 1

    def getConsonant_type(stem):
        # 0 = default; 1 = [+nas]_; 2 = [-voi]_; 3 = [+lat]_; 4 = [-cons]_
        if re.search("[мнң][ьъ]*$", stem) != None:
            return 1
        if re.search("[пфтцсчшщкх][ьъ]*$", stem) != None:
            return 2
        if re.search("[л][ьъ]*$", stem) != None:
            return 3
        if re.search("[аяыоюёуеэиөү][ьъ]*$", stem) != None:
            return 4
        return 0

    def inflect(stem, number, possessorPerson, possessorNumber, case):
        return(tuvanWord.markCASE(case, tuvanWord.markPOS(possessorPerson, possessorNumber, tuvanWord.markNUM(number, stem))))

    def markNUM(number, stem):

        if number != 2:
            return stem

        return stem + tuvanWord.L[tuvanWord.getConsonant_type(stem)] + tuvanWord.A[tuvanWord.getBackness(stem)] + "р"

    def markPOS(person, number, stem):
        
        backness = tuvanWord.getBackness(stem)
        rounding = tuvanWord.getRounding(stem)
        ctype = tuvanWord.getConsonant_type(stem)

        # Coronal fricatives voice intervocalically
        if re.search("(с([ъь])*$)", stem) != None:
            stem = stem[:len(stem)-1] + "з"
        if re.search("(ш([ъь])*$)", stem) != None:
            stem = stem[:len(stem)-1] + "ж"

        if person == 1 and number == 1:
            if ctype != 4:
                if (not tuvanWord.isMonosyllabic(stem)) and tuvanWord.velarCheck(stem):
                    stem = stem[:len(stem)-1]
                    stem = tuvanWord.hiatus_I(stem)
                else:
                    stem += tuvanWord.I[backness][rounding]
            stem += "м"
        
        elif person == 1 and number == 2:            
            if ctype != 4:
                if (not tuvanWord.isMonosyllabic(stem)) and tuvanWord.velarCheck(stem):
                    stem = stem[:len(stem)-1]
                    stem = tuvanWord.hiatus_I(stem)
                else:
                    stem += tuvanWord.I[backness][rounding]
            stem += ("в" + tuvanWord.I[backness][rounding] + "с")

        elif person == 2 and number == 1:
            if ctype != 4:
                if (not tuvanWord.isMonosyllabic(stem)) and tuvanWord.velarCheck(stem):
                    stem = stem[:len(stem)-1]
                    stem = tuvanWord.hiatus_I(stem)
                else:
                    stem += tuvanWord.I[backness][rounding]
            stem += "ң"

        elif person == 2 and number == 2:
            if ctype != 4:
                if (not tuvanWord.isMonosyllabic(stem)) and tuvanWord.velarCheck(stem):
                    stem = stem[:len(stem)-1]
                    stem = tuvanWord.hiatus_I(stem)
                else:
                    stem += tuvanWord.I[backness][rounding]
            stem += ("ң" + tuvanWord.A[backness] + "р")

        elif person == 3:
            if ctype != 4:
                if (not tuvanWord.isMonosyllabic(stem)) and tuvanWord.velarCheck(stem):
                    stem = stem[:len(stem)-1]
                    stem = tuvanWord.hiatus_I(stem)
                else:
                    stem += tuvanWord.I[backness][rounding]
            else:
                stem += ("з" + tuvanWord.I[backness][rounding])

        return stem

    def markCASE(case, stem):

        backness = tuvanWord.getBackness(stem)
        rounding = tuvanWord.getRounding(stem)
        ctype = tuvanWord.getConsonant_type(stem)

        if case == 1:
            return stem + tuvanWord.N[ctype] + tuvanWord.I[backness][rounding]

        if case == 2:
            return stem + tuvanWord.N[ctype] + tuvanWord.I[backness][rounding] + "ң"

        if case == 3:
            # Velar deletion?
            return stem + tuvanWord.G[ctype] + tuvanWord.A[backness]

        if case == 4:
            # Check
            return stem + tuvanWord.D[ctype] + tuvanWord.A[backness]

        if case == 5:
            # Check
            return stem + tuvanWord.D2[ctype] + tuvanWord.A[backness] + "н"

        if case == 6:
            print("Not done yet.")

        return stem

    def isMonosyllabic(stem):
        if not re.search("([иүыуэеөаояеёю]+[пбмвтднрсзлшжчйкгңхфцщь]+[иүыуэеөаояеёю]+)", stem):
            return 1
        return 0

    def velarCheck(stem):
        # Determines whether stem ends in a consonant that can undergo velar deletion
        return stem[-1] in ["к", "г"]

    def hiatus_I(stem):
        #This should only be used when the stem ends in a vowel and a suffix starting with
        # |I| is being added
        lastVowel = stem[-1]

        #Special patterns involving spelling
        if lastVowel == "я":
            return stem + "а"
        if lastVowel == "ю":
            return stem + "у"
        if lastVowel == "ё":
            return stem + "о"
        if lastVowel == "е":
            return stem[:len(stem)-1] + "ээ"
        
        return stem + lastVowel

    def hiatus_A(stem):
        #This should only be used when the stem ends in a vowel and a suffix starting with
        # |A| is being added
        lastVowel = stem[-1]

        #Special patterns involving spelling
        if lastVowel == "я":
            return stem + "а"
        if lastVowel == "ю":
            return stem[:len(stem)-1] + "яа"
        if lastVowel == "ё":
            return stem[:len(stem)-1] + "яа"
        if lastVowel == "е":
            return stem[:len(stem)-1] + "ээ"
        
        return stem[:len(stem)-1] + A[getBackness(stem)] + A[getBackness(stem)]

enuOut = open("generated_sentences_enu.align", "w", encoding = "utf-8")
tyvOut = open("generated_sentences_tyv.align", "w", encoding = "utf-8")

def paradigmRange(index1, index2, template, lang, npadd="", reverse=0, num1=[1,2], pos1=[0,1,2,3], pnum1=[1,2], case1=[0,1,2,3,4,5,6], num2=[1,2], pos2=[0,1,2,3], pnum2=[1,2], case2=[0,1,2,3,4,5,6]):
    sentences = []
    
    if lang is "enu":
        wordsource = englishNouns
        inflector = englishWord
    elif lang is "tyv":
        wordsource = tuvanNouns
        inflector = tuvanWord
    else: raise IOError
    
    for a in num1:
        for b in pos1:
            for c in pnum1:
                for d in case1:
                    for e in num2:
                        for f in pos2:
                            for g in pnum2:
                                for h in case2:
                                    if (not (b == 0 and c != 1)) and (not (f == 0 and g != 1)):
                                        if reverse == 0:
                                            sentences += (template.format(inflector.inflect(npadd + wordsource[index1], a, b, c, d), inflector.inflect(wordsource[index2], e, f, g, h)) + "\n")
                                        else:
                                            sentences += (template.format(inflector.inflect(npadd + wordsource[index2], e, f, g, h), inflector.inflect(wordsource[index1], a, b, c, d)) + "\n")

    return sentences

# Locative Sentences
i=0
while i < len(tuvanNouns):
    enuOut.write("".join(paradigmRange(i-1, i, "{!s} is {!s}.", "enu", npadd="one big ", num1=[1], case1=[0], case2=[4])))
    tyvOut.write("".join(paradigmRange(i-1, i, "{!s} бир улуг {!s} бар.", "tyv", reverse=1, num1=[1], case1=[0], case2=[4])))

    i+=1

# Comparative Sentences
i=0
while i < len(tuvanNouns):
    enuOut.write("".join(paradigmRange(i-1, i, "{!s} is bigger than {!s}.", "enu", num1=[1], case1=[0], case2=[0])))
    tyvOut.write("".join(paradigmRange(i-1, i, "{!s} {!s} улуг.", "tyv",  num1=[1], case1=[0], case2=[5])))

    i+=1

enuOut.close()
tyvOut.close()
print("Done.")

